    rsa_crt_pri_key->dq = dq;
    rsa_crt_pri_key->p = p;
    rsa_crt_pri_key->q = q;
    rsa_crt_pri_key->u = u;

    ret = get_rand(msg, sizeof(msg));

    for (i = HASH_MD5; i <= HASH_SHA512_256; i++)
    {
        hash_alg = (hash_alg_e)i;

        for (mLen = 0; mLen <= sizeof(msg); mLen++)
        {
            ret = rsa_ssa_pkcs1_v1_5_sign(HASH_SHA256, msg, mLen, d, n, 1024, signature1);
            if (RSA_SUCCESS != ret)
            {
                printf("\r\n rsa_ssa_pkcs1_v1_5_sign fail mLen=%u hash_alg=%u\n", mLen, hash_alg);
                printf("\r\n ret=%08x\n", ret);
                print_buf_u8(signature1, 1024 / 8, "signature");
                return 1;
            }

            ret |= rsa_ssa_pkcs1_v1_5_verify(HASH_SHA256, msg, mLen, e, 17, n, 1024, signature1);
            if (RSA_SUCCESS != ret)
            {
                printf("\r\n rsa_ssa_pkcs1_v1_5_verify fail mLen=%u hash_alg=%u\n", mLen, hash_alg);
                printf("\r\n ret=%08x\n", ret);
                print_buf_u8(signature1, 1024 / 8, "signature");
                return 1;
            }

            ret = rsa_ssa_pkcs1_v1_5_crt_sign(HASH_SHA256, msg, mLen, rsa_crt_pri_key, n, 1024, signature2);
            if ((RSA_SUCCESS != ret) || (0U != memcmp_(signature1, signature2, 1024 / 8)))
            {
                printf("\r\n rsa_ssa_pkcs1_v1_5_sign(private key is CRT style) fail "
                       "mLen=%u hash_alg=%u\n",
                       mLen, hash_alg);
                printf("\r\n ret=%08x\n", ret);
                print_buf_u8(signature2, 1024 / 8, "signature");
                return 1;
            }

            ret |= rsa_ssa_pkcs1_v1_5_verify(HASH_SHA256, msg, mLen, e, 17, n, 1024, signature2);
            if (RSA_SUCCESS != ret)
            {
                printf("\r\n rsa_ssa_pkcs1_v1_5_verify 2 fail mLen=%u hash_alg=%u\n", mLen, hash_alg);
                printf("\r\n ret=%08x\n", ret);
                print_buf_u8(signature2, 1024 / 8, "signature");
                return 1;
            }

            printf("%d-%d ", hash_alg, mLen);
        }
        printf("\n\n");
    }

    return 0;
