/**
 * @brief           Comprehensive function to run all RSA PKCS1 v1.5 tests
 * @return          0 if all tests pass, 1 if any test fails
 */
unsigned int rsassa_pkcs1_v1_5_test(void)
{
    if (rsassa_pkcs1_v1_5_2048_std_test())
    {
        return 1;
    }

    if (rsassa_pkcs1_v1_5_rand_traversal_test())
    {
        return 1;
    }

    return 0;
}

